public class IpcpDecode {

    /**
     * x-client-sign
     */
    public static final String SIGN = "716d61aa5e2a65c8847fd15eb6ba456ab627d97c449a178e160c7538d77e9861";
    /**
     * 数据
     */
    public static final String DATA = "";
    /**
     * 签名密钥
     */
    public static final String SECRET_KEY = "jQnTqVsYv2x4A7C9EcHeKhPkRnUrWtYw";
    /**
     * 解密密钥
     */
    public static final String AES_KEY = "54f61603c375c6ea486bc3b32f9470fa";

    public static void main(String[] args) {
        //解密
        String content = EncryptUtil.aes128Decode(AES_KEY, DATA);
        System.out.println(content);
        //判断hash值与x-client-sign的SIGN值是否相同
        String hash = EncryptUtil.bytesToHex(EncryptUtil.hmacSha256(SECRET_KEY, content));
        System.out.println(hash);

    }
}